public class Main {
    public static void main(String[] args) {

        BootStrapServices.getInstancia().init();
        UsuarioServ.getInstance().crear(new Usuario("admin", "admin", "admin", "admin"));
        Usuario eliam = UsuarioServ.getInstance().crear(new Usuario("eliam", "eliam", "1", "cliente"));
        Usuario zheng = UsuarioServ.getInstance().crear(new Usuario("zheng", "zheng", "1", "cliente"));

        // Productos.
        int aux = 0;
        for(int i = 0 ; i < 30 ; i++){
            Producto p = ProductoServ.getInstance().crear(new Producto("Winasorb "+i,10,15,"dolor",1));
            String encoder1 = "\n";
            String encoder2 = "\n";
            FotoServices.getInstancia().crear(new Foto(p,"foto"+i,"image/jpeg",encoder1));
            FotoServices.getInstancia().crear(new Foto(p,"foto"+i,"image/jpeg",encoder2));
            ComentarioServ.getInstance().crear(new Comentario(p,eliam,"Buen producto. Sirve mucho para los dolores."));
            ComentarioServ.getInstance().crear(new Comentario(p,zheng,"Buen precio y un gran servicio."));
        }

        Javalin app = Javalin.create(config ->{
            config.addStaticFiles(staticFileConfig -> {
                staticFileConfig.hostedPath = "/";
                staticFileConfig.directory = "/publico";
                staticFileConfig.location = Location.CLASSPATH;
            });
            //config.registerPlugin(new RouteOverviewPlugin("/rutas")); //aplicando plugins de las rutas
            config.enableCorsForAllOrigins();
        });
        app.start(getHerokuAssignedPort());

        new ProductoController(app).aplicarRutas();
        new UsuarioController(app).aplicarRutas();
        new PedidoController(app).aplicarRutas();

    }
    static int getHerokuAssignedPort() {
        ProcessBuilder processBuilder = new ProcessBuilder();
        if (processBuilder.environment().get("PORT") != null) {
            return Integer.parseInt(processBuilder.environment().get("PORT"));
        }
        return 7000; //Retorna el puerto por defecto en caso de no estar en Heroku.
    }

}
